import React, { useEffect, useState } from "react";

const Api = () => {
  const [user, setUser] = useState(null);

  useEffect(() => {
    fetch("https://api.randomuser.me/")
      .then((res) => res.json())
      .then((data) => {
        console.log(data);

        const userData = {
          name: data.results[0].name.first,
          email: data.results[0].email,
          picture: data.results[0].picture.thumnail,
        };

        setUser(userData);
      });
  }, []);

  return (
    <div>
      {user ? (
        <div>
          <div>
            <img src={user.picture} />
          </div>
          <div>
            <h4>{user.name}</h4>
            <p>{user.email}</p>
          </div>
        </div>
      ) : null}
    </div>
  );
};

export default Api;
